import React from 'react'
import { useState } from 'react'
import Sobj from './Sobj'
import { useEffect } from 'react'




const InputSection = () => {

    const [Array, setArray] = useState([]);
    useEffect(() => {
        console.log(Array);
      }, [Array]);


    const handleSubmit = () => {

        let iName = document.getElementById('idName').value
        let iDetails = document.getElementById('idDetails').value
        let iAmount = document.getElementById('idAmount').value
        let iDate = document.getElementById('idDate').value
        if (iAmount!==''){
        let newArray = {
            sname: iName,
            sdetails: iDetails,
            samount: iAmount,
            sdate: iDate 
        }
        setArray(newArray)
    }

    }


    return (
        <>
            <form className='mb-5' style={{border: '1px solid green', padding: '10px'}} >
                <div className="mb-3 ">
                        <label htmlFor="exampleInputEmail1" className="form-label">Name</label>
                    <input className="form-control" id="idName" type="text" placeholder="" />
                </div>
                <div className="mb-3 ">
                        <label htmlFor="exampleInputEmail1" className="form-label">In Details</label>
                    <input className="form-control" id="idDetails" type="text" placeholder="" />
                </div>
                <div className="mb-3 ">
                        <label htmlFor="exampleInputEmail1" className="form-label">Amount</label>
                    <input className="form-control" id="idAmount" type="number" placeholder="" />
                </div>
                <div className="mb-3 ">
                        <label htmlFor="exampleInputEmail1" className="form-label">Date</label>
                    <input className="form-control" id="idDate" type="date" placeholder="" />
                </div>

                <button type="button" onClick={handleSubmit} className="btn btn-primary">Submit</button>
            </form>

            {/* <Sobj Ndata = {Array} /> */}
            {/* {console.log(Array)} */}



        </>
    )
}

export default InputSection
